svg.script:

// Function to update path with dynamic y offset
function updatePath(yOffset) {
  var pathElement = document.getElementById('heart-path');
  var pathData = `M8 ${7.982 + yOffset} C9.664 ${6.309 + yOffset} 13.825 ${9.236 + yOffset} 8 ${13 + yOffset} 2.175 ${9.236 + yOffset} 6.336 ${6.309 + yOffset} 8 ${7.982 + yOffset}`;
  pathElement.setAttribute('d', pathData);
}

// Example usage: call updatePath with yOffset
updatePath(0.5); // Shift down by 5 units
